function addingDragAndDropSupport() {
   var nodeDivs = document.getElementsByClassName("node");
   var i;
   for(i=0; i<nodeDivs.length; i++){
      nodeDivs[i].draggable=true;
      nodeDivs[i].ondragend="drag(event)";
   }
}

