package de.uhd.ifi.se.decision.management.jira.persistence.releasenotespersistencemanager;

import org.junit.BeforeClass;

import com.atlassian.jira.user.ApplicationUser;

import de.uhd.ifi.se.decision.management.jira.TestSetUp;
import de.uhd.ifi.se.decision.management.jira.persistence.ReleaseNotesPersistenceManager;
import de.uhd.ifi.se.decision.management.jira.releasenotes.ReleaseNotes;
import de.uhd.ifi.se.decision.management.jira.testdata.JiraUsers;

public abstract class TestReleaseNotesPersistenceManagerSetUp extends TestSetUp {

	protected static ReleaseNotesPersistenceManager releaseNoteStrategy;
	protected static ApplicationUser user;
	protected static ReleaseNotes releaseNote;
	public static String aVeryLongContent;
	public static String projectKey;

	@BeforeClass
	public static void setUp() {
		init();
		releaseNoteStrategy = new ReleaseNotesPersistenceManager();
		user = JiraUsers.SYS_ADMIN.getApplicationUser();
		aVeryLongContent = "";
		projectKey = "TEST";
		releaseNote = new ReleaseNotes();
		releaseNote.setContent(aVeryLongContent);
		releaseNote.setProjectKey(projectKey);
	}
}
